import { useState } from 'react';
import { useHistory } from 'react-router-dom';



export const Login = () => {
  const history = useHistory();

  //pegar a senha e e-mails digitados no input. 
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const handleEntrar = () => {
    console.log(email)
  } 
  

  const handleClick = () => {
    history.push('/')
  }

  return (
    <div>
      <div>
        <form action="">

          <label htmlFor="">
            <span>Email </span>
            <input value={email} onChange={e => setEmail(e.target.value)} />
          </label>

          <label htmlFor="">
            <span>Senha </span>
            <input type="password" value={password} onClick={handleEntrar} onChange={e => setPass(e.target.value)} />
          </label>

          <button type="button">
            Entrar
          </button>
        </form>
      </div>







      <p>Login</p>
      <div>
        <button onClick={handleClick}>Pagina inicial</button>
      </div>
    </div>
  )
}
