import { SetStateAction, useEffect, useState } from "react";
import Loader from '../assets/loader.gif';



export function Home() {

  const [variavel, setVariavel] = useState('Luci')
  const [nomes, setNomes] = useState([])
  const [loading, setLoading] = useState(true)

  const trocarNome = (nome: SetStateAction<string>, index: number) => {
    console.log('Index desse nome ====>', index)
    setVariavel(nome)
  }

  useEffect(() => {
    console.log
    getNomes()
  }, [])

  const getNomes = () => {
    const nomesApi = [
      { Nome: 'Emma' },
      { Nome: 'Bia' },
      { Nome: 'Roberto' },
      { Nome: 'Lucas' },
      { Nome: 'Anna' },
      { Nome: 'Pedro' }
    ]

    type nomesApi = {
      Nome: string
    }

    setNomes(nomesApi)
    setTimeout(() => {
      setLoading(false)
    }, 3000)
  }

  return Loader ? <img src={Loader} alt='Loading' /> :

    <>

      {nomes.map((item, index) =>
        <div key={index}>
          <div>{item.nome}</div>
          <button onClick={() => trocarNome(item.nome, index)}>
            TROCAR NOME
          </button>
          <br />
        </div>
      )}
      <div>
        NOME ATUAL: {variavel}
      </div>
    </>
}
